import './login.css';
import { useHistory } from "react-router-dom"
import React, {useState, useEffect} from 'react'

const Login = () => {
    let history = useHistory()

    const [flag, setFlag] = useState(false)

    //Login
    const [userID, setUserID] = useState('')
    const [password, setPassword] = useState('')
    
    //Signup
    const [confirmPass, setConfirmPass] = useState('')
    const [fname, setFname] = useState('')
    const [lname, setLname] = useState('')
    const [dob, setDOB] = useState('')
    const [gender, setGender] = useState('')
    const [phone, setPhone] = useState('')

    return (
        <div className="LoginForm">
            {
                !flag && (
                    <div className="Login">
                        <h1>Hospital</h1>
                        <input type="text" placeholder="Username" onChange={(e) => {setUserID(e.target.value)}}/>
                        <input type="password" placeholder="Password" onChange={(e) => {setPassword(e.target.value)}}/>
                        
                        <div>
                            <b>New user?   </b>
                            <a  className="setFlag" onClick={() => {setFlag(true)}}>Register here</a>
                        </div>
                        <button>LOGIN</button>
                    </div>)
            }

            {
                flag && (
                    <div className="Login">

                        <h1>Hospital</h1>

                        <div>
                            <input className='name' type="text" placeholder="First Name" />
                            <input className='name' type="text" placeholder="Last name" />
                        </div>

                        <div>
                            <input className='dob' type="number" placeholder="DD" />
                            <input className='dob' type="number" placeholder="MM" />
                            <input className='dob' type="number" placeholder="YYYY" />
                        </div>

                        <input type="text" placeholder="Phone " />
                        
                        <div>
                            <select onChange={() => {}}>
                                <option>Which type of patient?</option>
                                <option value='IP'>Inpatient</option>
                                <option value='OP'>Outpatient</option>
                            </select>

                            <select onChange={() => {}}>
                                <option>Gender</option>
                                <option value='IP'>Male</option>
                                <option value='OP'>Female</option>
                                <option value='OP'>Other</option>
                            </select>
                        </div>
                        
                        <input type="text" placeholder="Username" />
                        <input type="password" placeholder="Password" />
                        <input type="password" placeholder="Comfirm password" />

                        

                        <div>
                            <b>Already have an account?  </b>
                            <a className="setFlag" onClick={() => {setFlag(false)}}>Login</a>
                        </div>

                        <button>SIGN UP</button>
                    </div>)
            }
        </div>
    );
}

export default Login
